const vec2 p = vec2(-0.74364388703, 0.13182590421);
const float r = 1.0;
const int I = 50;

vec2 c = p + vPos * r, z = c;
float n = 0.0;
for (int i = I;i > 0;i--) {
  if(z.x*z.x+z.y*z.y > 4.0) {
    n = float(i)/float(I);
    break;
  }
  z = vec2(z.x*z.x-z.y*z.y, 2.0*z.x*z.y) + c;
} 
gl_FragColor = color(n);

